import React, { useState } from 'react';

const LandingPage = ({ onLogin }) => {
  const [isLogin, setIsLogin] = useState(true);
  const [formData, setFormData] = useState({ username: '', email: '', password: '' });
  const [errorMessage, setErrorMessage] = useState('');

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();

    const endpoint = isLogin ? '/api/login' : '/api/register';

    fetch(`http://localhost:5000${endpoint}`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(formData),
    })
      .then((response) => response.json())
      .then((data) => {
        if (data.token) {
          localStorage.setItem('token', data.token);
          onLogin(); // Redirect to the authenticated homepage
        } else {
          setErrorMessage(data.message || 'Something went wrong');
        }
      })
      .catch(() => setErrorMessage('Server error, please try again later'));
  };

  return (
    <div className="container mt-5">
      <h1 className="text-center">Welcome to MealPrep Master</h1>
      <div className="card mx-auto mt-4" style={{ maxWidth: '400px' }}>
        <div className="card-body">
          <h4 className="text-center">{isLogin ? 'Login' : 'Register'}</h4>
          {errorMessage && <div className="alert alert-danger">{errorMessage}</div>}
          <form onSubmit={handleFormSubmit}>
            <div className="mb-3">
              <label className="form-label">Username</label>
              <input
                type="text"
                name="username"
                className="form-control"
                value={formData.username}
                onChange={handleInputChange}
                required
              />
            </div>
            {!isLogin && (
              <div className="mb-3">
                <label className="form-label">Email</label>
                <input
                  type="email"
                  name="email"
                  className="form-control"
                  value={formData.email}
                  onChange={handleInputChange}
                  required
                />
              </div>
            )}
            <div className="mb-3">
              <label className="form-label">Password</label>
              <input
                type="password"
                name="password"
                className="form-control"
                value={formData.password}
                onChange={handleInputChange}
                required
              />
            </div>
            <button type="submit" className="btn btn-primary w-100">
              {isLogin ? 'Login' : 'Register'}
            </button>
          </form>
          <div className="text-center mt-3">
            {isLogin ? (
              <p>
                Don't have an account?{' '}
                <button
                  type="button"
                  className="btn btn-link"
                  onClick={() => setIsLogin(false)}
                >
                  Sign up here
                </button>
              </p>
            ) : (
              <p>
                Already have an account?{' '}
                <button
                  type="button"
                  className="btn btn-link"
                  onClick={() => setIsLogin(true)}
                >
                  Login here
                </button>
              </p>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default LandingPage;
