import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Navbar from './components/Navbar';
import Home from './pages/Home';
import MealPlanner from './pages/MealPlanner';
import RecipeLibrary from './pages/RecipeLibrary';
import LandingPage from './pages/LandingPage';

const App = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [loading, setLoading] = useState(true);
  const BASE_URL = process.env.REACT_APP_BACKEND_URL || 'http://localhost:5000';

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      console.log('Verifying token...'); // Debugging log
      fetch(`${BASE_URL}/api/verify`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
        .then((response) => {
          if (!response.ok) throw new Error('Token invalid');
          return response.json();
        })
        .then(() => {
          setIsAuthenticated(true);
          console.log('Token verified successfully'); // Debugging log
        })
        .catch(() => {
          console.error('Token verification failed. Logging out.'); // Debugging log
          localStorage.removeItem('token');
          setIsAuthenticated(false);
        })
        .finally(() => setLoading(false));
    } else {
      setIsAuthenticated(false);
      setLoading(false);
    }
  }, [BASE_URL]);

  const handleLogin = async (username, password) => {
    try {
      console.log('Attempting login with:', { username, password }); // Debugging log

      const response = await fetch(`${BASE_URL}/api/login`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username, password }),
      });

      const data = await response.json();

      if (!response.ok) {
        console.error('Login failed:', data.message); // Debugging log
        throw new Error(data.message || 'Login failed');
      }

      console.log('Login successful, token:', data.token); // Debugging log
      localStorage.setItem('token', data.token);
      setIsAuthenticated(true);
    } catch (err) {
      console.error('Error during login:', err.message); // Debugging log
      alert('Login failed: ' + err.message);
    }
  };

  const handleLogout = () => {
    console.log('Logging out...'); // Debugging log
    localStorage.removeItem('token');
    setIsAuthenticated(false);
  };

  if (loading) {
    return <div className="container mt-5 text-center">Loading...</div>;
  }

  return (
    <Router>
      {isAuthenticated && <Navbar onLogout={handleLogout} />}
      <Routes>
        <Route
          path="/"
          element={isAuthenticated ? <Navigate to="/home" replace /> : <LandingPage onLogin={handleLogin} />}
        />
        <Route
          path="/home"
          element={isAuthenticated ? <Home /> : <Navigate to="/" replace />}
        />
        <Route
          path="/meal-planner"
          element={isAuthenticated ? <MealPlanner /> : <Navigate to="/" replace />}
        />
        <Route
          path="/recipe-library"
          element={isAuthenticated ? <RecipeLibrary /> : <Navigate to="/" replace />}
        />
        <Route path="*" element={<Navigate to="/" replace />} />
      </Routes>
    </Router>
  );
};

export default App;
