import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Navbar from './components/Navbar';
import Home from './pages/Home';
import MealPlanner from './pages/MealPlanner';
import RecipeLibrary from './pages/RecipeLibrary';
import LandingPage from './pages/LandingPage';

const App = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem('token');
    console.log('Token on load:', token); // Log token on initial load
    setIsAuthenticated(!!token);
  }, []);

  const handleLogin = () => {
    localStorage.setItem('token', 'sample-token'); // Mock token storage
    console.log('Token set:', localStorage.getItem('token')); // Confirm token storage
    setIsAuthenticated(true);
  };

  const handleLogout = () => {
    alert('Logout button clicked'); // Alert to confirm button click
    localStorage.removeItem('token');
    setIsAuthenticated(false);
    window.location.href = '/'; // Force reload
  };
  
   

  return (
    <Router>
     {isAuthenticated && <Navbar onLogout={handleLogout} />}
      <Routes>
        <Route
          path="/"
          element={isAuthenticated ? <Navigate to="/home" replace /> : <LandingPage onLogin={handleLogin} />}
        />
        <Route
          path="/home"
          element={isAuthenticated ? <Home /> : <Navigate to="/" replace />}
        />
        <Route
          path="/meal-planner"
          element={isAuthenticated ? <MealPlanner /> : <Navigate to="/" replace />}
        />
        <Route
          path="/recipe-library"
          element={isAuthenticated ? <RecipeLibrary /> : <Navigate to="/" replace />}
        />
        <Route path="*" element={<Navigate to="/" replace />} />
      </Routes>
    </Router>
  );
};

export default App;
